package sec02.exam01;

public class SignOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = -100;
		int result1 = +x;
		int result2 = -x;
		
		System.out.println(result1);
		System.out.println(result2);
		
		byte y = -100;
		//byte result3 = +y; 에러
		int result3 = -y;
		System.out.println(result3);
		
				
		

	}

}
